/**
 * Gets the SVG code for a given icon.
 */
function plutus_recipe_get_icon_svg( $icon, $size = 24 ) {
	return Class_Plutus_Recipe_Pro_Icons::get_svg( 'ui', $icon, $size );
}

function plutus_recipe_icon_svg( $icon, $size = 24 ) {
	echo Class_Plutus_Recipe_Pro_Icons::get_svg( 'ui', $icon, $size );
}

/**
 * Gets the SVG code for a given social icon.
 */
function plutus_recipe_get_social_icon_svg( $icon, $size = 24 ) {
	return Class_Plutus_Recipe_Pro_Icons::get_svg( 'social', $icon, $size );
}

function plutus_recipe_social_icon_svg( $icon, $size = 24 ) {
	echo Class_Plutus_Recipe_Pro_Icons::get_svg( 'social', $icon, $size );
}

/**
 * SVG Icons class
 *
 * This class is in charge of displaying SVG icons across the site.
 *
 * Place each <svg> source on its own array key, without adding the
 * both `width` and `height` attributes, since these are added dnamically,
 * before rendering the SVG code.
 *
 * All icons are assumed to have equal width and height, hence the option
 * to only specify a `$size` parameter in the svg methods.
 *
 */
class Class_Plutus_Recipe_Pro_Icons {
	/**
	 * Gets the SVG code for a given icon.
	 */
	public static function get_svg( $group, $icon, $size ) {
		if ( 'ui' == $group ) {
			$arr = self::$ui_icons;
		} elseif ( 'social' == $group ) {
			$arr = self::$social_icons;
		} else {
			$arr = array();
		}
		if ( array_key_exists( $icon, $arr ) ) {
			$repl = sprintf( '<svg width="%d" height="%d" aria-hidden="true" role="img" focusable="false" ', $size, $size );
			$svg  = preg_replace( '/^<svg /', $repl, trim( $arr[ $icon ] ) ); // Add extra attributes to SVG code.
			$svg  = preg_replace( "/([\n\t]+)/", ' ', $svg ); // Remove newlines & tabs.
			$svg  = preg_replace( '/>\s*</', '><', $svg ); // Remove white space between SVG tags.

			return $svg;
		}

		return null;
	}

